import { nanoid } from "nanoid"
import { ADD_PERSON } from '../constant'

const initState = [{id:nanoid(), name:Tom, age:18}]
export default addPerson = (preState=initState, action) => {
    const {type, data} = action;
    switch (type) {
        case ADD_PERSON:
            return [data,...preState]
        default:
            return preState
    }
}
