//引入Count的UI组件
import CountUI from '../../components/Count/index'

//引入connect用于连接UI组件与redux
import { connect } from 'react-redux'

//a函数返回的对象中的key就作为传递给UI组件props的key，value就作为传递给UI组件props的value——状态
function count () {
    return {n:900}
}
//b函数返回的对象中的key就作为传递给UI组件props的key，value就作为传递给UI组件props的value——操作状态的方法
function b () {
    return {jia:() => {console.log()}}
}
//使用connect()()创建并暴露一个Count的容器组件
export default connect(count,b)(CountUI)
