//引入Count的UI组件
import CountUI from '../../components/Count/index'
//引入action
import { createIncrementAction, createDecrementAction, createIncrementAsyncAction } from "../../redux/count_action"
//引入connect用于连接UI组件与redux
import { connect } from 'react-redux'

/*
// 映射状态
const mapStateToProps = state => ({ count: state })
// 映射操作状态的方法
const mapDispatchToProps = dispatch => (
    {
        jia: (number) => { dispatch(createIncrementAction(number)) },
        jian: (number) => { dispatch(createDecrementAction(number)) },
        asyncjia: (number, time) => { dispatch(createIncrementAsyncAction(number, time)) }
    }
)
*/

//使用connect()()创建并暴露一个Count的容器组件,connect函数需要传递两个参数，这两个参数的类型第一个是函数，第二个可以是函数或者对象，第一个参数是状态，第二个参数是操作状态的方法
export default connect(
    state => ({ count: state }),
    {
        jia: createIncrementAction,
        jian: createDecrementAction,
        asyncjia: createIncrementAsyncAction
    }
)(CountUI)
