import React, { Component } from 'react'
import store from '../../redux/store'

export default class Count extends Component {
    componentDidMount() {
        store.subscribe(() => {
            this.setState({})
        })
    }

    increment() {
        const { value } = this.selectNumber
        store.dispatch({
            type: 'increment',
            data: value
        })
    }
    decrement() {
        const { value } = this.selectNumber
        store.dispatch({
            type: 'decrement',
            data: value
        })
    }
    incrementIfOdd() {
        const { value } = this.selectNumber
        const { count } = store.getState()
        if (count % 2 !== 0) {
            store.dispatch({
                type: 'incrementIfOdd',
                data: value
            })
        }
    }
    incrementAsync() {
        const { value } = this.selectNumber
        setTimeout(() => {
            store.dispatch({
                type: 'incrementAsync',
                data: value
            })
        }, 500);

    }


    render() {
        return (
            <div>
                <h1>当前求和为：{store.getState()}</h1>
                <select ref={c => this.selectNumber = c}>

                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                </select>
                <botton onClick={this.increment}>+</botton>&nbsp;&nbsp;
                <botton onClick={this.decrement}>-</botton>&nbsp;&nbsp;
                <botton onClick={this.incrementIfOdd}>当前求和为奇数再加</botton>&nbsp;&nbsp;
                <botton onClick={this.incrementAsync}>异步加</botton>
            </div>
        )
    }
}
