import { nanoid } from "nanoid"
import { ADD_PERSON } from '../constant'

const initState = []
export default personReducer = (preState=initState, action) => {
    const {type, data} = action;
    switch (type) {
        case ADD_PERSON:
            return [data,...preState]
        default:
            return preState
    }
}
