import React from 'react';
import ReactDOM from 'react-dom';
import App from './App';

ReactDOM.render(
  <App />,
  document.getElementById('root')
);

//监测redux中状态的改变，如redux的状态发生可改变，那么重新渲染App组件
store.subscribe(() => {
  ReactDOM.render(
    <App />,
    document.getElementById('root')
  );
})
